/**
 * Author - Yathindra Kodithuwakku
 * Verify isbn
 * @type {{PREFIX, ISBN}|*}
 */
const regexp = require('./src/regexp')
const checksum = require('./src/checksum')

class ISBN {
    /**
     * Verify given isbn
     * @param isbn
     * @returns boolean
     * @constructor
     */
    static Verify(isbn) {
        //Remove optional prefix
        isbn = isbn.replace(regexp.PREFIX, '');

        if (!regexp.ISBN.test(isbn)) {
            return false
        }

        return checksum(isbn);
    }
}

module.exports = ISBN
